import{_ as e,c as l,o as t,ag as r}from"./chunks/framework.Bw-5EFTY.js";const u=JSON.parse('{"title":"量子化学概述","description":"","frontmatter":{},"headers":[],"relativePath":"分子模拟/背景知识/量子化学概述.md","filePath":"分子模拟/背景知识/量子化学概述.md","lastUpdated":null}'),i={name:"分子模拟/背景知识/量子化学概述.md"};function o(n,a,s,_,d,h){return t(),l("div",null,a[0]||(a[0]=[r('<h1 id="量子化学概述" tabindex="-1">量子化学概述 <a class="header-anchor" href="#量子化学概述" aria-label="Permalink to &quot;量子化学概述&quot;">​</a></h1><ul><li>量子化学计算基于<strong>量子力学</strong></li><li><strong>量子力学</strong>主要考虑电子的运动和影响</li><li>第一性原理计算是量子化学计算的代表性方法之一，它将电子自由度和核自由度分开考虑。该方法只需要原子的核电荷和少数模拟的环境参数作为输入，<strong>所有的性质都是通过求解薛定谔方程得到的</strong>，不需要使用任何经验参数。</li><li>量子化学计算的优点是结果的高精确性，常用于拟合和优化力场参数。缺点是，对于大体系，薛定谔方程求解速度很慢效率很低，因此限制了在大分子体系中的直接使用。</li></ul><h2 id="量子化学计算软件" tabindex="-1">量子化学计算软件 <a class="header-anchor" href="#量子化学计算软件" aria-label="Permalink to &quot;量子化学计算软件&quot;">​</a></h2><ol><li>GAMESS：GAMESS是一个从头计算量子化学程序包，#开源</li><li>GAUSSIAN: #商业软件, 具有图形用户界面, 使用更简单方便</li><li>VASP</li><li>NWChem</li><li>Material Studio 中也包含量子力学模块：CASTEP、Dmol3、Onetep、DFTB+、QMERA、VAMP</li></ol><h2 id="拓展阅读" tabindex="-1">拓展阅读 <a class="header-anchor" href="#拓展阅读" aria-label="Permalink to &quot;拓展阅读&quot;">​</a></h2><ul><li><a href="https://zhuanlan.zhihu.com/p/350746076" target="_blank" rel="noreferrer">分子模拟之量子化学</a></li><li><a href="https://blog.csdn.net/E_Magic_Cube/article/details/137096324" target="_blank" rel="noreferrer">理论计算入门手册——分子模拟、量子化学、第一性原理、有限元，最全概念</a></li></ul>',6)]))}const p=e(i,[["render",o]]);export{u as __pageData,p as default};
